import React, { useState } from 'react';
import props from 'prop-types';
import DoctorService from '../services/DoctorService';
import AppointmentService from '../services/AppointmentService';

class AddAppointment extends React.Component{



    constructor(props){
        super(props)
        this.state = {
            doctors:[],
            appointments:[],
            appDate:null,
            appDocID:null,
            appPacID:null,
            appTime:null,


        }
    }


    componentDidMount(){
        DoctorService.getDoctors().then((response)=>{
            this.setState({doctors: response.data})
        });
        AppointmentService.getAppointments().then((response)=>{
            this.setState({appointments: response.data})
        });
    }
    dateToString(given_date, delay=0){
        var date = new Date();
        date.setDate(given_date.getDate()+delay);
        var dd = String(date.getDate()).padStart(2, '0');
        var mm = String(date.getMonth() + 1).padStart(2, '0');
        var yyyy = date.getFullYear();

        return yyyy + '-' + mm + '-' + dd;

    }
    handlePressedButton = (event, data) =>{




        fetch('http://localhost:8080/api/appointment', {
        method: 'POST',
        // headers:{""}
        body: JSON.stringify(data)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });
    }
    dateHandleChange(event){
        this.setState({appDate:event.target.value})
    }
    doctorIdHandleChange(event){
        this.setState({appDocID:event.target.value})
    }
    render(){
        var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0');
        var yyyy = today.getFullYear();

        return(
            <>
                <form onSubmit={this.handlePressedButton()} method="post" id="appointment_form">

                    {this.state.doctors.map(doctor=>{
                        return(

                        //     <div className="doctorsInAppointmentCreating">
                        //     <input type="radio" id={doctor.id}
                        //     name={doctor.firstName + doctor.lastName} value={doctor.firstName + doctor.lastName}/>
                        //     <label htmlFor={doctor.firstName + doctor.lastName}>{doctor.firstName + doctor.lastName}</label>
                        // </div>
                            <>
                                <div className="doctorsInAppointmentCreating">
                                    <input type="radio" id={doctor.id}
                                    name="doctors" value={this.state.appDocID} onChange={(e)=>this.doctorIdHandleChange}/>
                                    <label htmlFor={doctor.firstName + doctor.lastName}>{doctor.firstName + doctor.lastName}</label>
                                </div>
                            </>



                        )
                    })}

                    <div className="dateInAppointmentCreating">

                            <label htmlFor="1">Data wizyty:</label>
                            <input onChange={(e)=>this.dateHandleChange} type="date" id="1234" name="1234" min={this.dateToString(today,2)} max={this.dateToString(today,16)} required/>
                            <input value={this.state.appTime} type="time" id="12345"name="12345" min='10' max='19'  step="1200"required/>


                    </div>
                    <button>Dodaj wizytÄ™</button>

                </form>
                <p>{this.state.appDate}</p>
           </> 
        )
    }
}

export default AddAppointment;
